import React from "react";
import "react-native-gesture-handler";
import { StyleSheet, Text, View, TouchableOpacity, Image} from "react-native";

const HomeScreen = ({ navigation }) => {
  return (
    <View style={{backgroundColor: "#D8D8D6",}}>

      <View style={styles.container}>
        <TouchableOpacity
          onPress = {() =>
            navigation.navigate('Profile')}
            >
          <View style= {styles.fp_btn_profile}>
            <Image source={require('./img/profile.png')}
              style={{height: 40, width: 40,}}/>
            <Text> Check your profile! </Text>
          </View>
        </TouchableOpacity>

        <TouchableOpacity
          onPress = {() =>
            navigation.navigate('Record')}
          >
          <View style={styles.fp_btn_record}>
            <Text>Record your Food</Text>
          </View>
        </TouchableOpacity>

        <TouchableOpacity
          onPress = {() =>
            navigation.navigate('Search')}
          >
          <View style={styles.fp_btn_search}>
            <Text> Search for Food </Text>
          </View>
        </TouchableOpacity>
      </View>
    </View>
  );
};
export default HomeScreen;


const styles = StyleSheet.create({
  container: {
    padding: 10,
    flexDirection: "column",
    justifyContent: "flex-end",

  },
  fp_btn_profile:{
    backgroundColor: '#A4BDBE',
    alignItems: "center",
    margin: 30,
    width: "auto",
    height: '15%',
    borderWidth: 5,
    borderRadius: 10,
    flexDirection: 'row',
  },
  fp_btn_record:{
    backgroundColor: "#D5B9B1",
    alignItems: "center",
    margin: 30,
    width: "auto",
    height: '15%',
    borderWidth: 5,
    borderRadius: 10,
  },
  fp_btn_search:{
    backgroundColor: "#739O86",
    alignItems: "center",
    margin: 30,
    width: "auto",
    height: '15%',
    borderWidth: 5,
    borderRadius: 10,
  }

});
